import React, {Component} from 'react';
import { DataTable, TableHeader} from 'react-mdl';
import {graphql} from 'react-apollo';
import {getProjectsQuery} from '../../queries/queries';



class BikeTable extends Component {

    displayProjects(){
        var data = this.props.data;
        if(data.loading){
          return(<div>loading..</div>);
        } else {
          return data.projects.map(project => {
            return(<td>{project.projectname}</td>);
          })
        }
      }

    render() {    

        return(
                <div>
                    <DataTable align="center"
                        shadow={0}
                        rows={[
                         {Project: this.displayProjects()}, 
                         {Distribution: 'NORAD', quantity: 4000, seaons: 1.25},
                         {Distribution: 'IRISH AID', quantity: 2400, seaons: 2.35}
                            ]}
                        >
                            <TableHeader name="Distribution" tooltip="Project">Total Distribution</TableHeader>
                            <TableHeader numeric name="quantity" tooltip="Number of Bikes Distributed">Quantity</TableHeader>
                            <TableHeader numeric name="price" tooltip="Number of Years Active">Seasons Active</TableHeader>
                            
                        </DataTable>

                </div>


        )
    }


}

export default graphql(getProjectsQuery)(BikeTable);
