import React, {Component} from 'react';
import {Grid, Cell} from 'react-mdl';
import {getDistrictsQuery} from '../../../queries/queries';

import graphql from 'react-apollo';
import DistrictDetails from './DistrictDetails';


class RemoveDistrict extends Component {

    
    constructor(props){
        super(props);
        this.state = {
            districtid: null,
            
        }
    }

    displayDistricts(){
        var data = this.props.getDistrictsQuery;
        if(data.loading){
            return(<option>Loading District ..</option>)
        } else {
            return data.projects.map(district => {
                return(
                    
                      <option key ={district.id} value={district.id}>{district.districtname}</option>
                    
                );
            })
        }
    }


    render(){
        return(
            <div>
            <h4 align ="center"> Remove District</h4>
            <div className="form-style-5">
                <form  >
                    <select onChange={(e) => {this.setState({districtid: e.target.value})}} >
                        <option>Choose Project To Delete</option>
                        
                       {this.displayDistricts()}
                        
                    </select>
                    <button type="submitDelete">Delete</button>
                </form>

                
                <h4 align="center">District Projects</h4>
                <DistrictDetails districtId = {this.state.districtid}/>
                
               

               
            
            </div>
            </div>

        )
    }
}

export default RemoveDistrict;
