import React, {Component} from 'react';
import{ graphql, compose} from 'react-apollo';

import {getProjectsQuery, deleteProjectMuattion} from '../../../queries/queries';

// add provision to refuse already added districts
// add "District Added message"
// 

class RemoveProject extends Component {

    constructor(props){
        super(props);
        this.state = {
            selected: null
        }
    }

    displayProjects(){
        var data = this.props.data;
        if(data.loading){
            return(<option>Loadinf Projects ..</option>)
        } else {
            return data.projects.map(project => {
                return(
                    <option key ={project.id} onSelect={(e)=> {this.setState({selected: project.id })}}>{project.projectname}</option>
                );
            })
        }
    }


    render(){
        return(
                <div>
                <h4 align ="center"> Remove Project</h4>
                <div className="form-style-5">
                    <form>
                        <select>
                            <option>Choose Project To Delete</option>
                            {this.displayProjects()}
                            console.log(this.props);
                        </select>
                    </form>
                    <div className="form-style-5">
                    <h5>{this.state.selected}</h5>
                    <p>Loading Details..</p>

                    </div>
                
                </div>
                </div>
       
         
            
        )
    }
}

export default graphql(getProjectsQuery)(RemoveProject);
