import React, {Component} from 'react';
import {graphql, compose} from 'react-apollo';
import {getDistrictsQuery, addProjectMutation} from '../../../queries/queries';

import {Grid, Cell} from 'react-mdl';


class AddBikes extends Component {

    constructor(props){
        super(props);
        this.state ={
            projectname: '',
            bikenumber: null,
            distributedyear: null,
            districtid: ''
        };
    }

    displayDistricts(){
        var data = this.props.getDistrictsQuery;
        console.log(data);
        if(data.loading){
            return(<option>Loading..</option>);
        } else {
            return data.didstricts.map(district => {
                return(<option>{district.districtid}</option>)
            })
        }
    }

    submitForm(e){
        e.preventDafault();
        this.props.addProjectMutation({
            variables: {
                projectname: this.state.projectname,
                bikenumber: Number.parseInt(this.state.bikenumber),
                distributedyear: Number.parseInt(this.state.distributedyear),
                districtid: this.state.districtid

            },
        });
    }

    render(){
        return(
            <div>

            <div className="form-style-5">
            <form onSubmit={this.submitForm.bind(this)}>
            <fieldset>
            
            <input type="text"  placeholder="Project Name"  onChange={(e=> this.setState({projectname: e.target.value}))}/>
            <input type="text"  placeholder="Number of Bikes Distributed"  onChange={(e=> this.setState({bikenumber: e.target.value}))}/>
            <input type="text"  placeholder="Distributed Year"  onChange={(e=> this.setState({distributedyear: e.target.value}))}/>
            <select onChange={(e=> this.setState({districtid: e.target.value}))}>
                <option >Select IPC</option>
                    {this.displayDistricts()}
            </select>
            
            </fieldset>
            <input type="submit" value="Save" />
            <input type="reset" value="Reset" />
            </form>
            </div>
            
             
            </div>
            
        )
    }
}

export default compose(
    graphql(getDistrictsQuery,{name: "getDistrictQuery"}),
    graphql(addProjectMutation,{name: "addProjectMutation"}))
    (AddBikes);
