import React, {Component} from 'react';
import {graphql} from 'react-apollo';
import {getDistrictsQuery} from '../../../queries/queries';

import {Grid, Cell} from 'react-mdl';


class AddBikes extends Component {
    displayDistricts(){
        var data = this.props.data;
        console.log(data);
    }
    render(){
        return(
            <div>

                  <div className="form-style-5">
            <form onSubmit={this.submitForm.bind(this)}>
            <fieldset>
            
            <input type="text"  placeholder="Project Name"  onChange={(e=> this.setState({projectname: e.target.value}))}/>
            <input type="text"  placeholder="Number of Bikes Distributed"  onChange={(e=> this.setState({bikenumber: e.target.value}))}/>
            <input type="text"  placeholder="Distributed Year"  onChange={(e=> this.setState({distributedyear: e.target.value}))}/>
            <select onChange={(e=> this.setState({districtid: e.target.value}))}>
                <option >Select IPC</option>
                    {this.displayDistricts()}
            </select>
            
            </fieldset>
            <input type="submit" value="Save" />
            <input type="reset" value="Reset" />
            </form>
            </div>
            
             
            </div>
            
        )
    }
}

export default graphql(getDistrictsQuery)(AddBikes);
