import React, {Component} from 'react';
import{ graphql, compose} from 'react-apollo';

import {addDistrictMutation} from '../../queries/queries';
import {Grid, Cell} from 'react-mdl';


class AddDistrict extends Component {

    constructor(props){
        super(props);
        this.state = {
            districtname: '',
        
        };
    }

    submitForm(e){
        
        e.preventDefault();
                console.log(this.state);
            this.props.addDistrictMutation({
                    variables:{
                        districtname: this.state.districtname,

                    },
                    
                });
            }   


    render(){
        return(
        
            <div class="form-style-5">
            <form onSubmit={this.submitForm.bind(this)}>
            <fieldset>
            
            <input type="text"  placeholder="IPC" onChange={(e=> this.setState({districtname: e.target.value}))}/>
            </fieldset>
            <input type="submit" value="Add" />
            </form>
            </div>
       
         
            
        )
    }
}

export default graphql(addDistrictMutation)(AddDistrict);
