import React, {Component} from 'react';
import {Grid, Cell} from 'react-mdl';
import {getProjectQuery} from '../queries/queries';
import graphql from 'react-apollo';


class Settings extends Component {


    displayProjectDetails(){
        const { project } = this.props.data;
          if(project){
              return(
                  <div>
                      <h3>Project Name: {project.projectname}</h3>
                      <p> Bikes Distributed: {project.bikenumber}</p>
                      <p> Season :{project.distributedyear}</p>
                      <p> IPC : {project.district.districtname}</p>
                  </div>
              );
          } else {
              return(
                  <div> No Project Selected </div>
              )
          }
      }


    render(){
        return(
            <div>
            <h2 align ="center">Settings</h2>
            <Grid>
                <Cell col={6}> 
                <h4 align ="center"> Remove Project</h4>
                <div className="form-style-5">
                    <form>
                        <select>
                            <option>Choose Project To Delete</option>
                        </select>
                    </form>
                    <div className="form-style-5">
                    <h5>Project Details</h5>
                    <p>Loading Details..</p>

                    </div>
                
                </div>
                
                
                
                </Cell>
                <Cell col={6}> 
                <h4 align ="center"> Remove District</h4>
                <div className="form-style-5">
                    <form>
                        <select>
                            <option>Choose IPC To Delete</option>
                        </select>
                    </form>
                    
                
                </div>
                
                
                
                </Cell>
            </Grid>
            
            
            
            
            
            
            </div>
        )
    }
}

export default Settings;
